        public string GetDateInYYYYMMDD(string dt)
        {
            if (dt == "1900-01-01")
            {
                return dt;
            }
            string[] stringSeparators = new string[] { "-" };
            string[] str = dt.Split('-');

            string tempdt = string.Empty;
            for (int i = 2; i >= 0; i += -1)
                tempdt += str[i] + "-";
            tempdt = tempdt.Substring(0, 10);
            return tempdt;
        }
        public string GetDateInYYYYMMDD1(string dt)
        {
            if (dt == "1900-01-01")
            {
                return dt;
            }
            string[] stringSeparators = new string[] { "/" };
            string[] str = dt.Split('/');

            string tempdt = string.Empty;
            for (int i = 2; i >= 0; i += -1)
                tempdt += str[i] + "-";
            tempdt = tempdt.Substring(0, 10);
            return tempdt;
        }

